package com.maswadkar.developers.androidify

import android.content.Intent
import android.os.Bundle
import android.view.View
import androidx.activity.enableEdgeToEdge
import androidx.appcompat.app.AlertDialog
import androidx.appcompat.app.AppCompatActivity
import androidx.appcompat.widget.Toolbar
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.firebase.auth.FirebaseAuth
import com.maswadkar.developers.androidify.data.ChatRepository
import com.maswadkar.developers.androidify.data.Conversation
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch

class HistoryActivity : AppCompatActivity() {

    private lateinit var historyAdapter: ChatHistoryAdapter
    private val chatRepository = ChatRepository.getInstance()

    companion object {
        const val EXTRA_CONVERSATION_ID = "conversation_id"
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContentView(R.layout.activity_history)

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(android.R.id.content)) { v, insets ->
            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom)
            insets
        }

        setupToolbar()
        setupRecyclerView()
        observeConversations()
    }

    private fun setupToolbar() {
        val toolbar = findViewById<Toolbar>(R.id.toolbar)
        setSupportActionBar(toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupRecyclerView() {
        val rvHistory = findViewById<RecyclerView>(R.id.rvHistory)

        historyAdapter = ChatHistoryAdapter(
            onConversationClick = { conversation ->
                openConversation(conversation)
            },
            onDeleteClick = { conversation ->
                showDeleteConfirmation(conversation)
            }
        )

        rvHistory.layoutManager = LinearLayoutManager(this)
        rvHistory.adapter = historyAdapter
    }

    private fun observeConversations() {
        val userId = FirebaseAuth.getInstance().currentUser?.uid ?: return

        lifecycleScope.launch {
            chatRepository.getConversationsFlow(userId).collectLatest { conversations ->
                historyAdapter.updateConversations(conversations)
                updateEmptyState(conversations.isEmpty())
            }
        }
    }

    private fun updateEmptyState(isEmpty: Boolean) {
        val rvHistory = findViewById<RecyclerView>(R.id.rvHistory)
        val emptyState = findViewById<View>(R.id.emptyState)

        if (isEmpty) {
            rvHistory.visibility = View.GONE
            emptyState.visibility = View.VISIBLE
        } else {
            rvHistory.visibility = View.VISIBLE
            emptyState.visibility = View.GONE
        }
    }

    private fun openConversation(conversation: Conversation) {
        val intent = Intent(this, MainActivity::class.java).apply {
            putExtra(EXTRA_CONVERSATION_ID, conversation.id)
            flags = Intent.FLAG_ACTIVITY_CLEAR_TOP or Intent.FLAG_ACTIVITY_SINGLE_TOP
        }
        startActivity(intent)
        finish()
    }

    private fun showDeleteConfirmation(conversation: Conversation) {
        AlertDialog.Builder(this)
            .setTitle(R.string.delete_conversation)
            .setMessage("Are you sure you want to delete \"${conversation.title}\"?")
            .setPositiveButton(android.R.string.ok) { _, _ ->
                deleteConversation(conversation.id)
            }
            .setNegativeButton(android.R.string.cancel, null)
            .show()
    }

    private fun deleteConversation(conversationId: String) {
        lifecycleScope.launch {
            chatRepository.deleteConversation(conversationId)
        }
    }
}

package com.maswadkar.developers.androidify

import android.content.Intent
import android.os.Bundle
import android.view.Menu
import android.view.MenuItem
import android.view.View
import android.widget.Button
import android.widget.EditText
import android.widget.TextView
import androidx.activity.enableEdgeToEdge
import androidx.activity.viewModels
import androidx.appcompat.app.AlertDialog
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import androidx.lifecycle.Lifecycle
import androidx.lifecycle.lifecycleScope
import androidx.lifecycle.repeatOnLifecycle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.firebase.auth.FirebaseAuth
import com.maswadkar.developers.androidify.auth.AuthRepository
import kotlinx.coroutines.launch

class MainActivity : AppCompatActivity() {

    private val viewModel: ChatViewModel by viewModels()
    private lateinit var chatAdapter: ChatAdapter
    private lateinit var historyAdapter: ChatHistoryAdapter
    private lateinit var authRepository: AuthRepository

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContentView(R.layout.activity_main)

        // Initialize auth repository
        authRepository = AuthRepository(this)

        // Check if user is logged in
        if (FirebaseAuth.getInstance().currentUser == null) {
            navigateToLogin()
            return
        }

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main)) { v, insets ->
            val imeInsets = insets.getInsets(WindowInsetsCompat.Type.ime() or WindowInsetsCompat.Type.systemBars())
            v.setPadding(imeInsets.left, imeInsets.top, imeInsets.right, imeInsets.bottom)
            insets
        }

        val rvChat = findViewById<RecyclerView>(R.id.rvChat)
        val etInput = findViewById<EditText>(R.id.etInput)
        val btnSend = findViewById<Button>(R.id.btnSend)
        val welcomeView = findViewById<View>(R.id.welcomeView)

        // History views
        val rvHistory = findViewById<RecyclerView>(R.id.rvHistory)
        val tvHistoryTitle = findViewById<TextView>(R.id.tvHistoryTitle)
        val tvNoHistory = findViewById<TextView>(R.id.tvNoHistory)

        // Setup example question click listeners
        setupExampleQuestions(etInput)

        // Setup chat adapter
        chatAdapter = ChatAdapter(mutableListOf())
        rvChat.layoutManager = LinearLayoutManager(this).apply {
            stackFromEnd = true // Start from bottom like a chat
        }
        rvChat.adapter = chatAdapter

        // Setup history adapter
        historyAdapter = ChatHistoryAdapter(
            onConversationClick = { conversation ->
                viewModel.loadConversation(conversation.id)
            },
            onDeleteClick = { conversation ->
                showDeleteConfirmation(conversation.id, conversation.title)
            }
        )
        rvHistory.layoutManager = LinearLayoutManager(this)
        rvHistory.adapter = historyAdapter

        // Observe messages from ViewModel
        lifecycleScope.launch {
            repeatOnLifecycle(Lifecycle.State.STARTED) {
                viewModel.messages.collect { messages ->
                    chatAdapter.updateMessages(messages)

                    // Show/hide welcome view based on messages
                    if (messages.isEmpty()) {
                        welcomeView.visibility = View.VISIBLE
                        rvChat.visibility = View.GONE
                    } else {
                        welcomeView.visibility = View.GONE
                        rvChat.visibility = View.VISIBLE
                        rvChat.scrollToPosition(messages.size - 1)
                    }
                }
            }
        }

        // Observe conversation history
        lifecycleScope.launch {
            repeatOnLifecycle(Lifecycle.State.STARTED) {
                viewModel.conversationsFlow.collect { conversations ->
                    historyAdapter.updateConversations(conversations)

                    // Show/hide history section based on conversations
                    if (conversations.isEmpty()) {
                        tvHistoryTitle.visibility = View.GONE
                        rvHistory.visibility = View.GONE
                        tvNoHistory.visibility = View.VISIBLE
                    } else {
                        tvHistoryTitle.visibility = View.VISIBLE
                        rvHistory.visibility = View.VISIBLE
                        tvNoHistory.visibility = View.GONE
                    }
                }
            }
        }

        btnSend.setOnClickListener {
            sendMessage(etInput)
        }
    }

    override fun onStop() {
        super.onStop()
        // Save conversation when app goes to background or closes
        viewModel.saveCurrentConversation()
    }

    private fun showDeleteConfirmation(conversationId: String, title: String) {
        AlertDialog.Builder(this)
            .setTitle("Delete Conversation")
            .setMessage("Are you sure you want to delete \"$title\"?")
            .setPositiveButton("Delete") { _, _ ->
                viewModel.deleteConversation(conversationId)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun setupExampleQuestions(etInput: EditText) {
        val exampleClickListener = View.OnClickListener { view ->
            val question = (view as TextView).text.toString()
            // Remove the quotes from the example question
            val cleanQuestion = question.trim('"')
            etInput.setText(cleanQuestion)
            sendMessage(etInput)
        }

        findViewById<TextView>(R.id.tvExample1).setOnClickListener(exampleClickListener)
        findViewById<TextView>(R.id.tvExample2).setOnClickListener(exampleClickListener)
        findViewById<TextView>(R.id.tvExample3).setOnClickListener(exampleClickListener)
    }

    private fun sendMessage(etInput: EditText) {
        val userText = etInput.text.toString().trim()
        if (userText.isNotEmpty()) {
            viewModel.sendMessage(userText)
            etInput.text.clear()
        }
    }

    override fun onCreateOptionsMenu(menu: Menu): Boolean {
        menuInflater.inflate(R.menu.menu_main, menu)
        return true
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.action_sign_out -> {
                signOut()
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    private fun signOut() {
        lifecycleScope.launch {
            authRepository.signOut()
            navigateToLogin()
        }
    }

    private fun navigateToLogin() {
        val intent = Intent(this, LoginActivity::class.java)
        intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
        startActivity(intent)
        finish()
    }
}

package com.maswadkar.developers.androidify

import android.content.Intent
import android.os.Bundle
import android.view.MenuItem
import android.view.View
import android.widget.Button
import android.widget.EditText
import android.widget.ImageView
import android.widget.TextView
import androidx.activity.OnBackPressedCallback
import androidx.activity.enableEdgeToEdge
import androidx.activity.viewModels
import androidx.appcompat.app.ActionBarDrawerToggle
import androidx.appcompat.app.AppCompatActivity
import androidx.appcompat.widget.Toolbar
import androidx.core.view.GravityCompat
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import androidx.drawerlayout.widget.DrawerLayout
import androidx.lifecycle.Lifecycle
import androidx.lifecycle.lifecycleScope
import androidx.lifecycle.repeatOnLifecycle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.google.android.material.navigation.NavigationView
import com.google.firebase.auth.FirebaseAuth
import com.maswadkar.developers.androidify.auth.AuthRepository
import kotlinx.coroutines.launch

class MainActivity : AppCompatActivity(), NavigationView.OnNavigationItemSelectedListener {

    private val viewModel: ChatViewModel by viewModels()
    private lateinit var chatAdapter: ChatAdapter
    private lateinit var authRepository: AuthRepository
    private lateinit var drawerLayout: DrawerLayout
    private lateinit var toggle: ActionBarDrawerToggle

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContentView(R.layout.activity_main)

        // Initialize auth repository
        authRepository = AuthRepository(this)

        // Check if user is logged in
        if (FirebaseAuth.getInstance().currentUser == null) {
            navigateToLogin()
            return
        }

        setupToolbarAndDrawer()
        setupViews()
        handleIntent(intent)
    }

    override fun onNewIntent(intent: Intent) {
        super.onNewIntent(intent)
        handleIntent(intent)
    }

    private fun handleIntent(intent: Intent) {
        // Check if we're loading a specific conversation from HistoryActivity
        val conversationId = intent.getStringExtra(HistoryActivity.EXTRA_CONVERSATION_ID)
        if (conversationId != null) {
            viewModel.loadConversation(conversationId)
        }
    }

    private fun setupToolbarAndDrawer() {
        val toolbar = findViewById<Toolbar>(R.id.toolbar)
        setSupportActionBar(toolbar)

        drawerLayout = findViewById(R.id.drawerLayout)
        toggle = ActionBarDrawerToggle(
            this,
            drawerLayout,
            toolbar,
            R.string.open_drawer,
            R.string.close_drawer
        )
        drawerLayout.addDrawerListener(toggle)
        toggle.syncState()

        // Handle back press to close drawer
        onBackPressedDispatcher.addCallback(this, object : OnBackPressedCallback(true) {
            override fun handleOnBackPressed() {
                if (drawerLayout.isDrawerOpen(GravityCompat.START)) {
                    drawerLayout.closeDrawer(GravityCompat.START)
                } else {
                    isEnabled = false
                    onBackPressedDispatcher.onBackPressed()
                }
            }
        })

        val navigationView = findViewById<NavigationView>(R.id.navigationView)
        navigationView.setNavigationItemSelectedListener(this)

        // Setup navigation header with user info
        setupNavHeader(navigationView)
    }

    private fun setupNavHeader(navigationView: NavigationView) {
        val headerView = navigationView.getHeaderView(0)
        val ivUserPhoto = headerView.findViewById<ImageView>(R.id.ivUserPhoto)
        val tvUserName = headerView.findViewById<TextView>(R.id.tvUserName)
        val tvUserEmail = headerView.findViewById<TextView>(R.id.tvUserEmail)

        val currentUser = FirebaseAuth.getInstance().currentUser
        currentUser?.let { user ->
            tvUserName.text = user.displayName ?: "User"
            tvUserEmail.text = user.email ?: ""

            // Load profile photo with Glide
            user.photoUrl?.let { photoUrl ->
                Glide.with(this)
                    .load(photoUrl)
                    .circleCrop()
                    .placeholder(R.drawable.ic_default_avatar)
                    .into(ivUserPhoto)
            }
        }
    }

    private fun setupViews() {
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main)) { v, insets ->
            val imeInsets = insets.getInsets(WindowInsetsCompat.Type.ime() or WindowInsetsCompat.Type.systemBars())
            v.setPadding(imeInsets.left, imeInsets.top, imeInsets.right, imeInsets.bottom)
            insets
        }

        val rvChat = findViewById<RecyclerView>(R.id.rvChat)
        val etInput = findViewById<EditText>(R.id.etInput)
        val btnSend = findViewById<Button>(R.id.btnSend)
        val welcomeView = findViewById<View>(R.id.welcomeView)

        // Setup example question click listeners
        setupExampleQuestions(etInput)

        // Setup chat adapter
        chatAdapter = ChatAdapter(mutableListOf())
        rvChat.layoutManager = LinearLayoutManager(this).apply {
            stackFromEnd = true
        }
        rvChat.adapter = chatAdapter

        // Observe messages from ViewModel
        lifecycleScope.launch {
            repeatOnLifecycle(Lifecycle.State.STARTED) {
                viewModel.messages.collect { messages ->
                    chatAdapter.updateMessages(messages)

                    // Show/hide welcome view based on messages
                    if (messages.isEmpty()) {
                        welcomeView.visibility = View.VISIBLE
                        rvChat.visibility = View.GONE
                    } else {
                        welcomeView.visibility = View.GONE
                        rvChat.visibility = View.VISIBLE
                        rvChat.scrollToPosition(messages.size - 1)
                    }
                }
            }
        }

        btnSend.setOnClickListener {
            sendMessage(etInput)
        }
    }

    override fun onNavigationItemSelected(item: MenuItem): Boolean {
        when (item.itemId) {
            R.id.nav_new_chat -> {
                viewModel.startNewConversation()
            }
            R.id.nav_history -> {
                startActivity(Intent(this, HistoryActivity::class.java))
            }
            R.id.nav_sign_out -> {
                signOut()
            }
        }
        drawerLayout.closeDrawer(GravityCompat.START)
        return true
    }

    override fun onStop() {
        super.onStop()
        viewModel.saveCurrentConversation()
    }

    private fun setupExampleQuestions(etInput: EditText) {
        val exampleClickListener = View.OnClickListener { view ->
            val question = (view as TextView).text.toString()
            val cleanQuestion = question.trim('"')
            etInput.setText(cleanQuestion)
            sendMessage(etInput)
        }

        findViewById<TextView>(R.id.tvExample1).setOnClickListener(exampleClickListener)
        findViewById<TextView>(R.id.tvExample2).setOnClickListener(exampleClickListener)
        findViewById<TextView>(R.id.tvExample3).setOnClickListener(exampleClickListener)
    }

    private fun sendMessage(etInput: EditText) {
        val userText = etInput.text.toString().trim()
        if (userText.isNotEmpty()) {
            viewModel.sendMessage(userText)
            etInput.text.clear()
        }
    }


    private fun signOut() {
        lifecycleScope.launch {
            authRepository.signOut()
            navigateToLogin()
        }
    }

    private fun navigateToLogin() {
        val intent = Intent(this, LoginActivity::class.java)
        intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
        startActivity(intent)
        finish()
    }
}
